    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        double lado, altura;
        try{lado = Double.parseDouble(txtLado.getText());
        altura = (lado)*(Math.sqrt(3))/2;
        txtPerimetro.setText(String.valueOf(3*lado));
        txtAltura.setText(String.valueOf(altura));
        txtArea.setText(String.valueOf((lado*altura)/2));
        txtRespuesta.setText("Se han introducido caracteres validos");
        }catch (NumberFormatException ex){
            txtRespuesta.setText("Se han introducido caracteres no numericos");
        }
    }//GEN-LAST:event_btnCalcularActionPerformed

